import React, { Component } from 'react'
import Product from './Product'
import data from './data.json'
import Modal from './Modal'

export default class ShoeShop extends Component {
    state = {
        isShow: false,
        dataShoe: data[0]


    }
    handleShoeDetail =() =>{
        this.setState({
            isShow:true

        })
    }
    handleShoe = (valueClick) => {
        this.setState({ dataShoe: valueClick })
    }

    render() {
        return (
            <div className='container '>
                <div className="row">
                    <div className="col-3">
                        <div className="d-flex mt-3 justify-content-center " > 

                        </div>

                        <div className="show">
                        <i className="fa-solid fa-cart-plus mr-3" style={{color:"white", fontSize:"30px",bca}}></i>
                            <span className="my-3 py-2 px-5" style={{backgroundColor: 'green', color: 'white',borderRadius: '5px',fontSize: '30px'}}  >Shoe</span>
                            {this.state.isShow &&(
                                <Modal content={this.state.dataShoe}/>
                            )}

                        </div>
                    </div>

                    <div className="col-9">
                        <div className="product_list">
                            <div className="row">
                                {
                                    data.map((value, index) => {
                                        return (
                                            <div className="col-4" key={value.id}>
                                                <Product product={value} handleShoeDetail={this.handleShoeDetail} handleShoe={this.handleShoe} />
                                            </div>

                                        )
                                    })
                                }

                            </div>
                        </div>


                    </div>

                </div>

            </div>
        )
    }
}
