import React, { Component } from 'react'
import Product from './Product'
import data from './data.json'
import Modal from './Modal'

export default class ShoeShop extends Component {
    state = {
        isShow: false,
        dataShoe: {
            "id": 2,
            "name": "Adidas Prophere Black White",
            "alias": "adidas-prophere-black-white",
            "price": 450,
            "description": "The adidas Primeknit upper wraps the foot with a supportive fit that enhances movement.\r\n\r\n",
            "shortDescription": "The midsole contains 20% more Boost for an amplified Boost feeling.\r\n\r\n",
            "quantity": 990,
            "image": "http://svcy3.myclass.vn/images/adidas-prophere-black-white.png"
        },

    }
    handleShoeDetail =() =>{
        this.setState({
            isShow:true

        })
    }
    handleShoe = (value) => {
        this.setState({ dataShoe: value })
    }

    render() {
        return (
            <div className='container '>
                <div className="row">
                    <div className="col-3">

                        <div className="show">
                            <h3 className="my-3 py-2" style={{backgroundColor: 'green', color: 'white',borderRadius: '5px'}}  >Shoe</h3>
                            {this.state.isShow &&(
                                <Modal detail={this.state.dataShoe}/>
                            )}

                        </div>
                    </div>

                    <div className="col-9">
                        <div className="product_list">
                            <div className="row">
                                {
                                    data.map((value, index) => {
                                        return (
                                            <div className="col-4" key={value.id}>
                                                <Product product={value} handleShoeDetail={this.handleShoeDetail} handleShoe={this.handleShoe} />
                                            </div>

                                        )
                                    })
                                }

                            </div>
                        </div>


                    </div>

                </div>

            </div>
        )
    }
}
