import React, { Component } from 'react'


export default class Modal extends Component {
  render() {
    const { detail } = this.props

    return (

        <div className="card">
          <img className="card-img-top" src={detail.image} alt={detail.name} />
          <div className="card-body">
            <div className="d-flex justify-content-around">
            <span className="card-title mr-2" style={{ fontSize:'18px', fontWeight:'bold'}}>{detail.name}</span>
            <span className="card-text pr-3" style={{ fontSize:'20px', fontWeight:'600',color:'green'}}>${detail.price} </span>

            </div>
            <p className="card-text" style={{color:'grey'}}>{detail.description}</p>
            {/* <p className="card-text" style={{ fontSize:'14px', fontWeight:'500'}}>Quantity: {detail.quantity} </p> */}

          </div>
        </div>



    )
  }
}
