import React, { Component } from 'react'


export default class Modal extends Component {
  render() {
    const { detail } = this.props

    return (

        <div className="card">
          <img className="card-img-top" src={detail.image} alt={detail.name} />
          <div className="card-body">
            <h4 className="card-title">{detail.name}</h4>
            <span className="card-text pr-3" style={{}}>{detail.price} $</span>
            <span className="card-text">Quantity: {detail.quantity} </span>
            <p className="card-text">{detail.description}</p>

          </div>
        </div>



    )
  }
}
