import React, { Component } from 'react'
import Modal from './Modal';

export default class Product extends Component {
    renderShoe =(producClick) =>{
        this.setState({
            data: producClick
        })

    }

    render() {
        const { product } = this.props;
        return (
            <div>
                {
                    <div className="card my-3">
                        <img className="card-img-top" width={100} src={product.image} alt={product.name} />
                        <div className="card-body">
                            <h4 className="card-title" style={{fontSize: '18px',fontWeight: '600',color: 'green'}}>{product.name}</h4>
                            <p className="card-text " style={{fontWeight: 'bold', fontSize: '25px'}}>{product.price} $ </p>
                            <button className="btn btn-dark mx-3">Add to cart</button>
                            <button className="btn btn-warning" onClick={() => handleShoe(product) ,handleShoeDetail(product)}>Detail</button>

                        </div>
                    </div>


                }
            </div>
        )
    }
}
