import React, { Component } from 'react'


export default class Modal extends Component {
  render() {
    const { detail } = this.props

    return (

        <div className="card text-left">
          <img className="card-img-top" src={detail.image} alt={detail.name} />
          <div className="card-body">

            <h3 className="card-title " style={{ fontSize:'20px', fontWeight:'bold'}}>{detail.name}</h3>
            <span className="card-text " style={{ fontSize:'20px', fontWeight:'600',color:'green'}}>${detail.price} </span>

            <span className="card-text ml-2" style={{ fontSize:'14px', fontWeight:'500'}}>Quantity: {detail.quantity} </span>

            <p className="card-text" style={{color:'grey'}}>{detail.description}</p>

          </div>
        </div>



    )
  }
}
